mapper.select:
 	SELECT COUNT(*) AS COUNT
	FROM USER_ESSENTIAL
	WHERE NAME = #{name} AND TEL = #{tel}

 		SELECT COUNT(*) AS COUNT
		FROM USER_ESSENTIAL
		WHERE NAME = #{name} AND EMAIL = #{email}

 		SELECT COUNT(*) AS COUNT
		FROM USER_ESSENTIAL
		WHERE UPPER(USERID) = UPPER(#{userId}) AND NAME = #{name} AND TEL = #{tel}

 		SELECT COUNT(*) AS COUNT
		FROM USER_ESSENTIAL
		WHERE UPPER(USERID) = UPPER(#{userId}) AND NAME = #{name} AND EMAIL = #{email}

SELECT USERID
FROM USER_ESSENTIAL
WHERE NAME = #{name} AND TEL = #{tel}

SELECT USERID
FROM USER_ESSENTIAL
WHERE NAME = #{name} AND EMAIL = #{email}
